import { ActionType } from "../contants/action-types";
const intial={
    products:[
        // {
        //     id:1,
        //     title:"Akdas",
        //     category:"Human"
        // },
    ],
};

export const productReducer =(state=intial,{type,payload})=>{
    switch (type)
    {
        case ActionType.SET_PRODUCTS:
        {
            return {...state,products:payload};
        }
        default:
        {
            return state;
        }
    }
};
export default productReducer;
